using System;using System.Threading;namespace ChatBot{    internal class Program    {        static void Main(string[] args)        {            String name;            String schedule;            TypeWithEffect("Medcare: Hello! Welcome to MedCare, your instant healthcare assistant. How can I help you today?\n");            Console.WriteLine();            String[] services = { "Book an appointment", "Reschedule an appointment", "Cancel an appointment", "Other inquiries" };            for (int i = 0; i < services.Length; i++)            {                Console.WriteLine(services[i]);            }            while (true)            {                Console.Write("\nYou: ");                String choice = Console.ReadLine();                switch (choice)                {                    case "Book an appointment":                        TypeWithEffect("\nMedCare: Great! Let's book your appointment. Can you provide the following details?");                        Console.Write("\n\nFull name: ");                        name = Console.ReadLine();                        Console.Write("Contact Number: ");                        String number = Console.ReadLine();                        Console.WriteLine("\nHospital Department/Specialist:");                        String[] department = { "Pediatrics", "Cardiology", "Dermatology", "OB-GYN", "Ophthalmology", "ENT", "Laboratory Services", "Orthopedics" };                        for (int i = 0; i < department.Length; i++)                        {                            Console.WriteLine(department[i]);                        }                        Console.Write("\nDepartment or Specialist: ");                        String dept = Console.ReadLine();                        Console.Write("Preferred Date and Time (format: yyyy-MM-dd HH:mm): ");                        schedule = Console.ReadLine();                        TypeWithEffect("\nMedCare: Thank you! Your appointment with " + dept + " is scheduled for " + schedule + ".");                        ConfirmAppointment();                        FurtherQuestion();                        return;                    case "Reschedule an appointment":                        TypeWithEffect("\nMedCare: Sure! Can you provide your current appointment details?");                        Console.Write("\n\nFull Name: ");                        name = Console.ReadLine();                        Console.Write("Appointment Date and Time (format: yyyy-MM-dd HH:mm): ");                        schedule = Console.ReadLine();                        TypeWithEffect("\nMedCare: Got it! Please provide your preferred new date and time.");                        Console.Write("\n\nNew Date and Time (format: yyyy-MM-dd HH:mm): ");                        String newSched = Console.ReadLine();                        TypeWithEffect("\nMedCare: Your appointment has been rescheduled to " + newSched + ".");                        FurtherQuestion();                        return;                    case "Cancel an appointment":                        TypeWithEffect("\nMedCare: I'm sorry to hear you'd like to cancel. Could you provide the following details to process the cancellation?");                        Console.Write("\n\nFull Name: ");                        name = Console.ReadLine();                        Console.Write("Appointment Date and Time (format: yyyy-MM-dd HH:mm): ");                        schedule = Console.ReadLine();                        TypeWithEffect("\nMedCare: Your appointment for " + schedule + " has been cancelled. Let us know if you'd like to book another one in the future!");                        return;                    case "Other inquiries":                        TypeWithEffect("\nMedCare: Could you please describe your inquiry so I can assist you better?");                        Console.Write("\n\nYou: ");                        String response = Console.ReadLine();                        if (response.ToLower() == "walk-ins")                        {                            TypeWithEffect("\nMedCare: Yes, we do accept walk-ins, but we recommend booking an appoinment to avoid long waiting times.");                            FurtherQuestion();                        }                        return;                    default:                        Console.WriteLine("Invalid input. Try Again");                        break;                }            }        }        static void ConfirmAppointment()        {            TypeWithEffect(" Would you like a confirmation via SMS or email?");            Console.Write("\n\nYou: ");            String confirmation = Console.ReadLine();            if (confirmation.ToLower() == "sms")                TypeWithEffect("\nMedCare: Done! You'll receive a confirmation message on your SMS shortly.");            else if (confirmation.ToLower() == "email")                TypeWithEffect("\nMedCare: Done! You'll receive a confirmation via email. Please check your inbox.");        }        static void FurtherQuestion()        {            TypeWithEffect(" Is there anything else I can help you with?");            Console.Write("\n\nYou: ");            String response = Console.ReadLine();            if (response.ToLower() == "no, thank you" || response.ToLower() == "nothing")            {                TypeWithEffect("\nMedCare: You're welcome! Thank you for choosing MedCare. Stay safe and healthy!");            }        }        static void TypeWithEffect(string text)        {            foreach (char c in text)            {                Console.Write(c);                Thread.Sleep(60);            }        }    }}